	private Session getSessionServerPosta(EmailRequest emailRequest) {
		if (emailRequest.isPec()) {
			return getSessionSMTPS();
		}
		return getSessionSMTP();
	}
	
	private Session getSessionSMTP() {
		if (sessionSMTP==null) {
			Properties props = new Properties();
			props.put("mail.smtp.host", EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_HOST));
			props.put("mail.smtp.port", EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_PORT));
			props.put("mail.smtp.connectiontimeout", EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_CONNECTIONTIMEOUT));
			props.put("mail.smtp.timeout", EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_TIMEOUT));
			props.put("mail.smtp.auth", EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_AUTH));
			
			sessionSMTP = Session.getInstance(props, new javax.mail.Authenticator() {
				protected PasswordAuthentication getPasswordAuthentication() {
					return new PasswordAuthentication(
						EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_AUTH_USERNAME), 
						EnvProperties.readFromFile(EnvProperties.MAIL_SMTP_AUTH_PASSWORD));
				}
			});
		}
		return sessionSMTP;
	}
	
	private Session getSessionSMTPS() {
		if (sessionSMTPS==null) {
			Properties props = new Properties();
			props.put("mail.smtp.host", EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_HOST));
//			props.put("mail.smtp.port", port);
			props.put("mail.smtp.socketFactory.class", "javax.net.ssl.SSLSocketFactory");
			props.put("mail.smtp.socketFactory.port", EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_PORT));
			props.put("mail.smtp.ssl.enable", "true");
			props.put("mail.smtp.ssl.checkserveridentity", true); // NEW 20230309 Sonar VULNERABILITY
			props.put("mail.smtp.starttls.enable", "true");
			props.put("mail.smtp.ssl.protocols", "TLSv1.2");
			props.put("mail.smtp.connectiontimeout", EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_CONNECTIONTIMEOUT));
			props.put("mail.smtp.timeout", EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_TIMEOUT));
			props.put("mail.smtp.auth", EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_AUTH));

			sessionSMTPS = Session.getInstance(props, new javax.mail.Authenticator() {
				protected PasswordAuthentication getPasswordAuthentication() {
					return new PasswordAuthentication(
							EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_AUTH_USERNAME), 
							EnvProperties.readFromFile(EnvProperties.MAIL_SMTPS_AUTH_PASSWORD));
				}
			});
		}
		return sessionSMTPS;
	}
	
	private String getFrom(EmailRequest emailRequest) {
		if (emailRequest.isPec()) {
			return FROM_SMTPS;
		}
		return FROM_SMTP;
	}
	
	
	@Override
	public void sendMail(EmailRequest emailRequest) throws BusinessException {
		if(isRequestContainsAttach(emailRequest)) {
			sendMailWithAttach(emailRequest);
		} else {
			sendMailSemplice(emailRequest);
		}
	}
	private boolean isRequestContainsAttach(EmailRequest emailRequest) {
		if (emailRequest.getAttachment()!=null && (emailRequest.getAttachment().getIdIstanza()!=null 
				|| emailRequest.getAttachment().getIdFile()!=null
				 || emailRequest.getAttachment().getIdNotifica()!=null )) {
			return true;
		}
		return false;
	}
	
	private void sendMailSemplice(EmailRequest emailRequest) throws BusinessException {
		final String methodName = "sendMailSemplice";
		final long start = System.currentTimeMillis();
		try {
			Message message = initMessageFromToSubject(emailRequest);
			message.setText(emailRequest.getText());
			Transport.send(message);
			if (LOG.isDebugEnabled()) {
				logSentMessageSuccessfullyTo(emailRequest, methodName);
			}
		} catch (BusinessException be) {
			LOG.warn("[" + CLASS_NAME + "::" + methodName + "] BusinessException");
			throw be;
		} catch (MessagingException e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] MessagingException to:"+emailRequest.getTo()+"\n"+emailRequest+"\n", e);
			trace(e, DecodificaError.MOONSRV_00401_EMAIL_MESSAGING.getError(), CLASS_NAME, methodName, emailRequest, start);
			throw new BusinessException(DecodificaError.MOONSRV_00401_EMAIL_MESSAGING.getError());
		} catch (Exception e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] Exception to:"+emailRequest.getTo()+"\n"+emailRequest+"\n", e);
			trace(e, DecodificaError.MOONSRV_00400_EMAIL_GENERICA.getError(), CLASS_NAME, methodName, emailRequest, start);
			throw new BusinessException(DecodificaError.MOONSRV_00400_EMAIL_GENERICA.getError());
        } finally {
            logFinally(methodName, emailRequest, start);
        }
	}



	
