	private Message initMessageFromToSubject(EmailRequest emailRequest) throws BusinessException {
		final String methodName = "initMessageFromToSubject";
		Message message = null;
		try {
			message = new MimeMessage(getSessionServerPosta(emailRequest));
			
			message.setFrom(new InternetAddress(getFrom(emailRequest)));
			// TO
			if(emailRequest.getTo().contains(";")) {
				message.setRecipients(Message.RecipientType.TO, extractedAddress(emailRequest.getTo()));
			}else{
				message.setRecipient(Message.RecipientType.TO, new InternetAddress(emailRequest.getTo()));
			}

			// CC
			if (StringUtils.isNotEmpty(emailRequest.getCc())) {
				if(emailRequest.getCc().contains(";")) {
					message.setRecipients(Message.RecipientType.CC, extractedAddress(emailRequest.getCc()));
				}else{
					message.setRecipient(Message.RecipientType.CC, new InternetAddress(emailRequest.getCc()));
				}
			}
			
			// BCC
			if (StringUtils.isNotEmpty(emailRequest.getBcc())) {
				if (!emailRequest.isPec()) {
					if(emailRequest.getBcc().contains(";")) {
						message.setRecipients(Message.RecipientType.BCC, extractedAddress(emailRequest.getBcc()));
					}else{
						message.setRecipient(Message.RecipientType.BCC, new InternetAddress(emailRequest.getBcc()));
					}
				} else {
					LOG.warn("EMAIL DESTINATION PEC ! Rimosso BCC " + emailRequest.getBcc());
				}
			}
			
			message.setSubject(SUBJECT_PREFIX_AMBIENTE + emailRequest.getSubject());
		} catch (Exception e) {
			LOG.error("[" + CLASS_NAME + "::initMessageFromToSubject] Exception to:"+emailRequest.getTo()+"\n"+emailRequest, e);
			trace(e, DecodificaError.MOONSRV_00402_EMAIL_INIT_FROMTOSUBJECT.getError(), CLASS_NAME, methodName, emailRequest);
			throw new BusinessException(DecodificaError.MOONSRV_00402_EMAIL_INIT_FROMTOSUBJECT.getError());
		}
		return message;
	}


	private InternetAddress[] extractedAddress(String multipleRecipients) throws AddressException {
		List<InternetAddress> list = new ArrayList<>();
		String[] median = multipleRecipients.split(";");
		for(int i=0;i<median.length;i++){
			list.add(new InternetAddress(median[i]));
		}
		InternetAddress[] address =list.toArray(new InternetAddress[list.size()]);
		return address;
	}
	
	
