	@Override
	public void sendMailHtmlAndText(EmailRequest emailRequest) throws BusinessException {
		if(isRequestContainsAttach(emailRequest)) {
			sendMailHtmlAndTextWithAttach(emailRequest);
		} else {
			sendMailHtmlAndTextSemplice(emailRequest);
		}
	}
	
	private void sendMailHtmlAndTextSemplice(EmailRequest emailRequest) throws BusinessException {
		final String methodName = "sendMailHtmlAndTextSemplice";
        long start = System.currentTimeMillis();
		try {
			Message message = initMessageFromToSubject(emailRequest);
		    Multipart htmlAndTextMultipart = new MimeMultipart( "alternative" );

		    MimeBodyPart textPart = new MimeBodyPart();
		    textPart.setText( emailRequest.getText(), "utf-8" );

		    MimeBodyPart htmlPart = new MimeBodyPart();
		    StringBuilder contentHtml = new StringBuilder(HEADER_HTML.replace("@@IMG_SRC_MOON_HEADER@@",getMoonUrl(MOON_VERY_SMALL_DATA_IMAGE_URL)));
		    contentHtml.append(emailRequest.getHtml());
		    contentHtml.append(FOOTER_HTML);
		    htmlPart.setContent( contentHtml.toString(), "text/html; charset=utf-8" );
		    
		    htmlAndTextMultipart.addBodyPart( textPart );
		    htmlAndTextMultipart.addBodyPart( htmlPart );
		    message.setContent( htmlAndTextMultipart );

		    // Required magic (violates principle of least astonishment).
		    message.saveChanges();
			Transport.send(message);
			
			logSentMessageSuccessfullyTo(emailRequest, methodName);
		} catch (BusinessException be) {
			LOG.warn("[" + CLASS_NAME + "::" + methodName + "] BusinessException");
			throw be;
		} catch (MessagingException e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] MessagingException to:"+emailRequest.getTo()+"\n"+emailRequest, e);
			throw new BusinessException("Errore di invio email", "MOONSRV-00401");
		} catch (Exception e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] Exception to:"+emailRequest.getTo()+"\n"+emailRequest, e);
			throw new BusinessException(DecodificaError.MOONSRV_00400_EMAIL_GENERICA.getError());
        } finally {
            logFinally(methodName, emailRequest, start);
        }
	}
	
	@Override
	public void sendMailHtmlAndTextWithAttach(EmailRequest emailRequest) throws BusinessException {
		final String methodName = "sendMailHtmlAndTextWithAttach";
        long start = System.currentTimeMillis();
		try {
			Message message = initMessageFromToSubject(emailRequest);
		    Multipart multipart = new MimeMultipart("mixed");
		    
		    Multipart htmlAndTextMultipart = new MimeMultipart( "alternative" );
		    MimeBodyPart textPart = new MimeBodyPart();
		    textPart.setText(emailRequest.getText(), "utf-8");
	
		    MimeBodyPart htmlPart = new MimeBodyPart();
		    StringBuilder contentHtml = new StringBuilder(HEADER_HTML.replace("@@IMG_SRC_MOON_HEADER@@",getMoonUrl(MOON_VERY_SMALL_DATA_IMAGE_URL)));
		    contentHtml.append(emailRequest.getHtml());
		    contentHtml.append(FOOTER_HTML);
		    htmlPart.setContent( contentHtml.toString(), "text/html; charset=utf-8" );
		    
		    htmlAndTextMultipart.addBodyPart( textPart );
		    htmlAndTextMultipart.addBodyPart( htmlPart );
		    
		    // stuff the multipart into a bodypart and add the bodyPart to the mainMultipart
		    MimeBodyPart htmlAndTextBodyPart = new MimeBodyPart();
		    htmlAndTextBodyPart.setContent(htmlAndTextMultipart);
		    multipart.addBodyPart(htmlAndTextBodyPart);

			multipart = addBodyPartAttach(emailRequest, multipart);
		    
		    //
		    message.setContent(multipart);
		    message.saveChanges(); // Required magic (violates principle of least astonishment).
			Transport.send(message);
			
			logSentMessageSuccessfullyTo(emailRequest, methodName);
		} catch (BusinessException be) {
			LOG.warn("[" + CLASS_NAME + "::" + methodName + "] BusinessException");
			throw be;
		} catch (MessagingException e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] MessagingException to:"+emailRequest.getTo()+"\n"+emailRequest, e);
			throw new BusinessException(DecodificaError.MOONSRV_00400_EMAIL_GENERICA.getError());
		} catch (Exception e) {
			LOG.error("[" + CLASS_NAME + "::" + methodName + "] Exception to:"+emailRequest.getTo()+"\n"+emailRequest, e);
			throw new BusinessException(DecodificaError.MOONSRV_00401_EMAIL_MESSAGING.getError());
        } finally {
            logFinally(methodName, emailRequest, start);
        }
	}


	private Multipart addBodyPartAttach(EmailRequest emailRequest, Multipart multipart) throws MessagingException, Exception {
		LOG.debug("[" + CLASS_NAME + "::addBodyPartAttach]");
		EmailAttachment attachRequest = emailRequest.getAttachment();
		if (attachRequest!=null && attachRequest.getIdIstanza()!=null) {
			addBodyPartAttachIstanzaById(multipart, emailRequest.getAttachment().getIdIstanza());
			addBodyPartAttachAllegatiIfRequested(multipart, attachRequest);
		}
		multipart = addBodyPartAttachByIdFile(emailRequest, multipart, attachRequest);
		multipart = addBodyPartAttachRepositoryFileByIdNotifica(multipart, attachRequest);
		multipart = addBodyPartAttachRepositoryFileByIdStoricaWorkflow(multipart, attachRequest);		
		return multipart;
	}

	protected Multipart addBodyPartAttachAllegatiIfRequested(Multipart multipart, EmailAttachment attachRequest)
			throws Exception {
		if (Boolean.TRUE.equals(attachRequest.getAllegati())) {
			List<AllegatoEntity> allegati = allegatoDAO.findByIdIstanza(attachRequest.getIdIstanza());
			for (AllegatoEntity allegato : allegati) {
				addBodyPartAttachAllegato(multipart, allegato);
			}
		}
		return multipart;
	}

	protected Multipart addBodyPartAttachByIdFile(EmailRequest emailRequest, Multipart multipart,
			EmailAttachment attachRequest) throws Exception {
		if (attachRequest!=null && attachRequest.getIdFile()!=null) {
			addBodyPartAttachFileById(multipart, emailRequest.getAttachment().getIdFile());
		}
		return multipart;
	}

	protected Multipart addBodyPartAttachRepositoryFileByIdNotifica(Multipart multipart, EmailAttachment attachRequest)
			throws Exception {
		if (attachRequest!=null && attachRequest.getIdNotifica()!=null) {
			List<RepositoryFileLazyEntity> allegati = repositoryFileDAO.findLazyByIdNotifica(attachRequest.getIdNotifica());
			LOG.debug("[" + CLASS_NAME + "::addBodyPartAttach] findedByIdNotifica : " + ((allegati==null)?"null":allegati.size()));
			for (RepositoryFileLazyEntity allegato : allegati) {
				addBodyPartAttachFileById(multipart, allegato.getIdFile());
			}
		}
		return multipart;
	}

	protected Multipart addBodyPartAttachRepositoryFileByIdStoricaWorkflow(Multipart multipart,
			EmailAttachment attachRequest) throws Exception {
		if (attachRequest!=null && attachRequest.getIdStoricoWorkflow()!=null) {
			List<RepositoryFileLazyEntity> allegati = repositoryFileDAO.findLazyByIdIstanzaStoricoWf(attachRequest.getIdIstanza(), attachRequest.getIdStoricoWorkflow());
			for (RepositoryFileLazyEntity allegato : allegati) {
				addBodyPartAttachFileById(multipart, allegato.getIdFile());
			}
		}
		return multipart;
	}
	
	private Multipart addBodyPartForApi(EmailRequest emailRequest, Multipart multipart)
			throws MessagingException, Exception {
		EmailAttachment attachRequest = emailRequest.getAttachment();

		if (attachRequest != null) {
			if (attachRequest.getIdIstanza()!=null) {
				if (Boolean.TRUE.equals(attachRequest.getIstanza())) {
					multipart = addBodyPartAttachIstanzaById(multipart, emailRequest.getAttachment().getIdIstanza());
				}
				multipart = addBodyPartAttachAllegatiIfRequested(multipart, attachRequest);
			}
			if (Boolean.TRUE.equals(attachRequest.getAllegatiAzione())) {
				multipart = addBodyPartAttachRepositoryFileByIdStoricaWorkflow(multipart, attachRequest);
			}
		}
		return multipart;
	}


	private Multipart addBodyPartAttachIstanzaById(Multipart mainMultipart, Long idIstanza) throws DAOException, MessagingException, Exception {
		byte[] bytes = printIstanzeService.getPdfById(idIstanza);
		IstanzaEntity istanzaE = istanzaDAO.findById(idIstanza);
		// attach file body parts directly to the mainMultipart
		MimeBodyPart filePart = new MimeBodyPart();
		InputStream is = new ByteArrayInputStream(bytes);
		filePart.setDataHandler(dataHandlerFromInputStream(is));
		filePart.setFileName(istanzaE.getCodiceIstanza()+".pdf");
		mainMultipart.addBodyPart(filePart);
		return mainMultipart;
	}
	
	private Multipart addBodyPartAttachAllegato(Multipart mainMultipart, AllegatoLazyEntity allegato) throws DAOException, MessagingException, Exception {
		// attach file body parts directly to the mainMultipart
		MimeBodyPart filePart = new MimeBodyPart();
		InputStream is = new ByteArrayInputStream(retrieveContenutoAllegato(allegato));
		filePart.setDataHandler(dataHandlerFromInputStream(is));
		if (allegato.getNomeFile() != null && !allegato.getNomeFile().equals(""))
		{
			filePart.setFileName(allegato.getNomeFile());
		}
		else {
			filePart.setFileName(allegato.getFormioNameFile());
		}
		mainMultipart.addBodyPart(filePart);
		return mainMultipart;
	}
	
	private byte[] retrieveContenutoAllegato(AllegatoLazyEntity allegato) {
		// TODO Gestione UUID INDEX se contenuto spostato su INDEX (poco probabile)
		return allegatoDAO.findById(allegato.getIdAllegato()).getContenuto();
	}

	
	private Multipart addBodyPartAttachFileById(Multipart mainMultipart, Long idFile) throws DAOException, MessagingException, Exception {
		LOG.debug("[" + CLASS_NAME + "::addBodyPartAttachFileById] IN idFile = " + idFile);
		RepositoryFileEntity file = repositoryFileDAO.findById(idFile);
		// attach file body parts directly to the mainMultipart
		MimeBodyPart filePart = new MimeBodyPart();
		InputStream is = new ByteArrayInputStream(file.getContenuto());
		filePart.setDataHandler(dataHandlerFromInputStream(is));
		filePart.setFileName(file.getNomeFile()); // RicevutaAccettazione.pdf
		mainMultipart.addBodyPart(filePart);
		return mainMultipart;
	}
	
