	private DataHandler dataHandlerFromInputStream(InputStream inputStream) throws Exception {
		if (LOG.isDebugEnabled()) {
			LOG.debug("[" + CLASS_NAME + "::dataHandlerFromInputStream] IN "+inputStream);
		}
		int readCount;
		byte[] buffer = new byte[1024];
		BufferedInputStream bufferedInputStream = new BufferedInputStream(inputStream);
		ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
		while( (readCount = bufferedInputStream.read(buffer)) > 0) {
//			LOG.debug("[" + CLASS_NAME + "::dataHandlerFromInputStream] read and write, up to 1024.");
		    byteArrayOutputStream.write(buffer, 0, readCount);
		}
		if (LOG.isDebugEnabled()) {
			LOG.debug("[" + CLASS_NAME + "::dataHandlerFromInputStream] byte[] = "+byteArrayOutputStream.toByteArray());
		}
		DataHandler result = new DataHandler(byteArrayOutputStream.toByteArray(), "application/octet-stream");
		if (LOG.isDebugEnabled()) {
			LOG.debug("[" + CLASS_NAME + "::dataHandlerFromInputStream] OUT "+result);
		}
	    return result;
	}

