	private void trace(Exception e, MoonError error, String className, String methodName, EmailRequest emailRequest, Optional<Long> start) throws BusinessException {
		final long end = System.currentTimeMillis();
		final long msec = start.isPresent()?(end - start.get()):0;
		try {
			if (LOG.isDebugEnabled()) {
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] =================================================================================");
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] className = " + className);
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] methodName = " + methodName);
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] error = " + error);
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] e.name = " + e.getClass().getName());
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] e.length() = " + ExceptionUtils.getStackTrace(e).length());
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] e.stackTrace = " + ExceptionUtils.getStackTrace(e));
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] e.message = " + ExceptionUtils.getMessage(e));
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] e.rootCauseMessage = " + ExceptionUtils.getRootCauseMessage(e));
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] emailRequest = " + emailRequest);
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] getInetAddress = " + getInetAddress());
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] elaps (ms) = " + msec);
				LOG.debug("[" + CLASS_NAME + "::throwAndTrace] =================================================================================");
			}
		} catch (Exception ex) {
			LOG.warn("[" + CLASS_NAME + "::throwAndTrace] Exception LOG.debug ", ex);
		}
		try {
			ErroreEntity ee = new ErroreEntity();
			ee.setUuid(UUID.randomUUID().toString());
			ee.setIdComponente(EnumComponent.MOONSRV.value);
			ee.setInetAdress(getInetAddress());
			ee.setDataIns(new Timestamp(System.currentTimeMillis()));
			ee.setAttoreIns(null);
			ee.setCodice(error!=null?error.getCode():null);
			ee.setMessage(error!=null?error.getMsg():null);
			ee.setClassName(className);
			ee.setMethodName(methodName);
			ee.setIdIstanza(emailRequest!=null&&emailRequest.getContext()!=null?emailRequest.getContext().getIdIstanza():null);
			ee.setIdModulo(emailRequest!=null&&emailRequest.getContext()!=null?emailRequest.getContext().getIdModulo():null);
			ee.setExClassName(e.getClass().getName());
			ee.setExMessage(pruneExceptionString(ExceptionUtils.getMessage(e)));
			ee.setExCause(pruneExceptionString(ExceptionUtils.getRootCauseMessage(e)));
			ee.setExTrace(pruneExceptionString(ExceptionUtils.getStackTrace(e)));
			ee.setInfo(emailRequest!=null?emailRequest.toString():null);
			ee.setElapsedTimeMs(msec);
			LOG.debug("[" + CLASS_NAME + "::throwAndTrace] INSERT ...");
			erroreDAO.insert(ee);
		} catch (Exception ex) {
			LOG.error("[" + CLASS_NAME + "::throwAndTrace] TraceException", ex);
		}
	}

	private String pruneExceptionString(String stringExcetion) {
		return stringExcetion==null?null:stringExcetion.replace("\\$", "");
	}

	protected void logDebugCharAt(String trace, int pos) {
		LOG.debug("[" + CLASS_NAME + "::throwAndTrace] charAt(1898) = " + trace.charAt(pos) + " - " + (int)trace.charAt(pos));
	}
	private void trace(Exception e, MoonError error, String className, String methodName, EmailRequest emailRequest) throws BusinessException {
		trace(e, error, className, methodName, emailRequest, Optional.empty());
	}
	private void trace(Exception e, MoonError error, String className, String methodName, EmailRequest emailRequest, long start) throws BusinessException {
		trace(e, error, className, methodName, emailRequest, Optional.of(start));
	}
	
	private static String getInetAddress() {
		try {
			InetAddress ip = InetAddress.getLocalHost();
			return "" + ip;
		} catch (UnknownHostException e) {
			LOG.error("[" + CLASS_NAME + "::getInetAddress] UnknownHostException ", e);
			return null;
		}
	}
	
	// LOG
	protected void logFinally(final String methodName, EmailRequest emailRequest, long start) {
		long end = System.currentTimeMillis();
		float sec = (end - start); 
		if (LOG.isDebugEnabled()) {
			logDebugMillisecondi(methodName, emailRequest, sec);
		} else {
			logInfoMillisecondi(methodName, sec);
		}
	}
	protected void logSentMessageSuccessfullyTo(EmailRequest emailRequest, final String methodName) {
		LOG.debug("[" + CLASS_NAME + "::" + methodName + "] Sent message successfully to "+emailRequest.getTo());
	}
	protected void logInfoMillisecondi(final String methodName, float sec) {
		LOG.info(methodName + "() in " + sec + " milliseconds.");
	}
	protected void logDebugMillisecondi(final String methodName, EmailRequest emailRequest, float sec) {
		LOG.debug(methodName + "("+emailRequest+") in " + sec + " milliseconds.");
	}
	
